// src/components/InterviewPage.js

import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/client';

export default function InterviewPage() {
  const { sessionId } = useParams();

  const [session, setSession] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [currentAnswer, setCurrentAnswer] = useState('');
  const [timer, setTimer] = useState(0);
  const [feedback, setFeedback] = useState(null);
  const [flags, setFlags] = useState([]);
  const [finishing, setFinishing] = useState(false);
  const [finishError, setFinishError] = useState('');

  const recorderRef = useRef(null);
  const streamRef = useRef(null);
  const [recordedBlob, setRecordedBlob] = useState(null);

  // 1. Load session & timer
  useEffect(() => {
    async function loadSession() {
      const res = await api.get(`sessions/${sessionId}/`);
      setSession(res.data);
      const startMs = new Date(res.data.started_at).getTime();
      const endMs = startMs + 30 * 60 * 1000;
      setTimer(Math.max(0, Math.floor((endMs - Date.now()) / 1000)));
    }
    loadSession();
  }, [sessionId]);

  // 2. Poll questions
  useEffect(() => {
    let iq;
    async function fetchQs() {
      const res = await api.get(
        `session-questions/?session=${sessionId}&ordering=asked_at`
      );
      const list = Array.isArray(res.data)
        ? res.data
        : res.data.results ?? [];
      setQuestions(list);
    }
    fetchQs();
    iq = setInterval(fetchQs, 2000);
    return () => clearInterval(iq);
  }, [sessionId]);

  // 3. Countdown
  useEffect(() => {
    if (timer <= 0) return;
    const tid = setInterval(() => {
      setTimer((t) => {
        if (t <= 1) {
          clearInterval(tid);
          handleFinish();
          return 0;
        }
        return t - 1;
      });
    }, 1000);
    return () => clearInterval(tid);
  }, [timer]);

  // 4. Start recording
  useEffect(() => {
    async function startRec() {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      streamRef.current = stream;
      const recorder = new MediaRecorder(stream, { mimeType: 'video/webm' });
      const chunks = [];
      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'video/webm' });
        setRecordedBlob(blob);
        stream.getTracks().forEach((t) => t.stop());
      };
      recorder.start();
      recorderRef.current = recorder;
    }
    startRec();
    return () => {
      if (recorderRef.current && recorderRef.current.state !== 'inactive') {
        recorderRef.current.stop();
      }
    };
  }, []);

  // Submit answer
  const submitAnswer = async (sq) => {
    if (!currentAnswer.trim()) return;
    await api.patch(`session-questions/${sq.id}/`, {
      answer_text: currentAnswer,
    });
    setCurrentAnswer('');
  };

  // Finish interview helper
  const handleFinish = async () => {
    if (finishing) return;
    setFinishing(true);
    setFinishError('');
    console.log('🚀 Finishing interview session', sessionId);

    try {
      const res = await api.patch(`sessions/${sessionId}/`, {
        status: 'completed',
      });
      console.log('✔️ Session patched:', res.data);

      if (
        recorderRef.current &&
        recorderRef.current.state !== 'inactive'
      ) {
        console.log('✂️ Stopping recorder');
        recorderRef.current.stop();
      }
    } catch (err) {
      console.error('❌ finishInterview error', err);
      const msg =
        err.response?.data?.detail ||
        err.response?.data ||
        err.message ||
        'Unknown error';
      setFinishError(msg);
    } finally {
      setFinishing(false);
    }
  };

  // Auto‐finish from timer
  const finishInterview = () => handleFinish();

  // Upload video & poll feedback
  useEffect(() => {
    if (!recordedBlob) return;
    async function uploadAndFetch() {
      setFeedback(null);
      console.log('📤 Uploading video blob for session', sessionId);
      const form = new FormData();
      form.append('session', sessionId);
      form.append('video_url', recordedBlob, 'interview.webm');
      await api.post('videos/', form);

      // capture old summary
      let oldSummary = null;
      try {
        const pre = await api.get(`feedback/?session=${sessionId}`);
        const preList = Array.isArray(pre.data)
          ? pre.data
          : pre.data.results ?? [];
        if (preList.length) oldSummary = preList[0].summary;
      } catch {}

      // poll until changed
      while (true) {
        const fbRes = await api.get(`feedback/?session=${sessionId}`);
        const list = Array.isArray(fbRes.data)
          ? fbRes.data
          : fbRes.data.results ?? [];
        if (list.length) {
          const latest = list[0];
          if (!oldSummary || latest.summary !== oldSummary) {
            console.log('🎉 New feedback arrived');
            setFeedback(latest);
            break;
          }
        }
        await new Promise((r) => setTimeout(r, 2000));
      }

      // fetch flags
      const flagsRes = await api.get(
        `flags/?recording__session=${sessionId}`
      );
      const flagList = Array.isArray(flagsRes.data)
        ? flagsRes.data
        : flagsRes.data.results ?? [];
      setFlags(flagList);
    }
    uploadAndFetch();
  }, [recordedBlob, sessionId]);

  // Render
  if (!session) return <div>Loading…</div>;

  if (feedback) {
    return (
      <div style={{ padding: '2rem' }}>
        <h2>Interview Completed</h2>
        <h3>
          Overall Score: {feedback.detailed_breakdown?.total_score ?? 'N/A'}
        </h3>
        <p>{feedback.summary}</p>
        <h4>Detailed Breakdown</h4>
        <pre>{JSON.stringify(feedback.detailed_breakdown, null, 2)}</pre>
        {flags.length > 0 && (
          <>
            <h4>Cheating Flags</h4>
            <ul>
              {flags.map((f) => (
                <li key={f.id}>
                  [{f.timestamp}] <strong>{f.flag_type}</strong>: {f.description}
                </li>
              ))}
            </ul>
          </>
        )}
      </div>
    );
  }

  const lastQ = questions[questions.length - 1] || {};
  const unanswered = lastQ.question && !lastQ.answer_text;
  const mins = String(Math.floor(timer / 60)).padStart(2, '0');
  const secs = String(timer % 60).padStart(2, '0');

  return (
    <div style={{ display: 'flex', padding: '2rem' }}>
      <div style={{ flex: 1, marginRight: '2rem' }}>
        <h2>Interview Session #{sessionId}</h2>
        <h3>
          Time Remaining: {mins}:{secs}
        </h3>
        {questions.map((q) => (
          <div
            key={q.id}
            style={{
              border: '1px solid #ccc',
              padding: '1rem',
              marginBottom: '1rem',
            }}
          >
            <p>
              <strong>Q:</strong>{' '}
              {q.question ? q.question.text : <em>Loading question…</em>}
            </p>
            {q.answer_text && (
              <>
                <p>
                  <strong>Your Answer:</strong> {q.answer_text}
                </p>
                {q.score != null && (
                  <p>
                    <strong>Score:</strong> {q.score} (confidence{' '}
                    {Math.round(q.confidence * 100)}%)
                  </p>
                )}
              </>
            )}
          </div>
        ))}

        {finishError && (
          <div style={{ color: 'red', marginBottom: '1rem' }}>
            <strong>Error finishing interview:</strong> {finishError}
          </div>
        )}

        {unanswered ? (
          <>
            <textarea
              rows={4}
              style={{ width: '100%' }}
              value={currentAnswer}
              onChange={(e) => setCurrentAnswer(e.target.value)}
            />
            <button
              onClick={() => submitAnswer(lastQ)}
              style={{ marginTop: '0.5rem' }}
            >
              Submit Answer
            </button>
          </>
        ) : (
          <p>Waiting for the next question…</p>
        )}

        <button
          onClick={finishInterview}
          disabled={finishing}
          style={{ marginTop: '1rem' }}
        >
          {finishing ? 'Finishing…' : 'Finish Interview Now'}
        </button>
      </div>
    </div>
  );
}
