import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/client';

export default function ResumeUpload() {
  const [file, setFile] = useState(null);
  const [resumes, setResumes] = useState([]);
  const [selected, setSelected] = useState(null);
  const navigate = useNavigate();

  // Fetch existing resumes on mount
  useEffect(() => {
    async function fetchResumes() {
      try {
        const res = await api.get('resumes/');
        setResumes(res.data.results);
      } catch (err) {
        console.error(err);
      }
    }
    fetchResumes();
  }, []);

  // File input handler
  const onFileChange = e => {
    setFile(e.target.files[0]);
  };

  // Upload a new resume
  const onUpload = async () => {
    if (!file) return alert('Please select a file first.');
    const form = new FormData();
    form.append('file', file);
    try {
      await api.post('resumes/', form, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      // Refresh the list
      const res = await api.get('resumes/');
      setResumes(res.data.results);
      setFile(null);
    } catch (err) {
      console.error(err);
      alert('Upload failed.');
    }
  };

  // Start an interview for the selected resume
  const onStartInterview = async () => {
    if (!selected) return alert('Please select a resume.');
    try {
      const res = await api.post('sessions/', { resume_id: selected });
      navigate(`/interview/${res.data.id}`);
    } catch (err) {
      console.error(err);
      alert('Could not start interview.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Upload Your Resume</h2>
      <input
        type="file"
        accept=".pdf,.doc,.docx"
        onChange={onFileChange}
      />
      <button onClick={onUpload} disabled={!file}>
        Upload
      </button>

      <h3>Your Resumes</h3>
      <ul>
        {resumes.map(r => (
          <li key={r.id} style={{ margin: '0.5rem 0' }}>
            <label>
              <input
                type="radio"
                name="resume"
                value={r.id}
                checked={selected === r.id}
                onChange={() => setSelected(r.id)}
              />{' '}
              {new Date(r.uploaded_at).toLocaleDateString()}{' '}
              &ndash;{' '}
              <a href={r.file} target="_blank" rel="noopener noreferrer">
                Download
              </a>
            </label>
          </li>
        ))}
      </ul>

      <button onClick={onStartInterview} disabled={!selected}>
        Start Interview
      </button>
    </div>
  );
}
