import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import ResumeUpload from './components/ResumeUpload';
// you’ll add InterviewPage later
// import InterviewPage from './components/InterviewPage';

function App() {
  const isLoggedIn = !!localStorage.getItem('token');
  return (
    <Router>
      <Switch>
        {/* Redirect to login if you add auth */}
        <Route path="/upload-resume">
          {isLoggedIn ? <ResumeUpload /> : <Redirect to="/login" />}
        </Route>
        {/* Placeholder for InterviewPage */}
        <Route path="/interview/:sessionId">
          {/* {isLoggedIn ? <InterviewPage /> : <Redirect to="/login" />} */}
          <div>Interview page coming soon…</div>
        </Route>
        {/* Fallback */}
        <Route path="/">
          <Redirect to="/upload-resume" />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
