// src/components/ResumeUpload.js

import React, { useState, useEffect } from 'react';
import api from '../api/client';

export default function ResumeUpload({ onSelect }) {
  const [resumes, setResumes] = useState([]);
  const [selectedId, setSelectedId] = useState(null);

  // Fetch resumes on mount
  useEffect(() => {
    async function loadResumes() {
      try {
        const res = await api.get('resumes/');
        // DRF pagination: results might be in res.data.results
        const list = Array.isArray(res.data)
          ? res.data
          : res.data.results ?? [];
        setResumes(list);
      } catch (err) {
        console.error('Failed to load resumes:', err);
      }
    }
    loadResumes();
  }, []);

  // Helper to extract filename from the URL
  const getFilename = (url) => {
    try {
      const parts = url.split('/');
      // decode URI in case of spaces or special chars
      return decodeURIComponent(parts[parts.length - 1]);
    } catch {
      return url;
    }
  };

  const handleStart = () => {
    if (selectedId) onSelect(selectedId);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Select a Resume</h2>
      {resumes.length === 0 && <p>No resumes uploaded yet.</p>}
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {resumes.map((r) => (
          <li key={r.id} style={{ marginBottom: '0.5rem' }}>
            <label>
              <input
                type="radio"
                name="resume"
                value={r.id}
                checked={selectedId === r.id}
                onChange={() => setSelectedId(r.id)}
                style={{ marginRight: '0.5rem' }}
              />
              {getFilename(r.file)}
            </label>
          </li>
        ))}
      </ul>
      <button
        disabled={!selectedId}
        onClick={handleStart}
        style={{ marginTop: '1rem' }}
      >
        Start Interview
      </button>
    </div>
  );
}
